mapper.update:
UPDATE auth_order
SET salesId=#{salesId}, payClientKey=#{payClientKey}, category=#{category}, paymentId= #{paymentId},
    price=#{price}, deposit=#{deposit}, ldCommission=#{ldCommission}, dealerCommission=#{dealerCommission},
    salesCommission=#{salesCommission}, status=#{status}, description=#{description}, txFee=#{txFee}
WHERE paymentId=#{paymentId}

mapper.select:
SELECT sum(salesCommission) commission, DAYOFMONTH(tradingTime) day
FROM auth_order
WHERE tradingTime between #{start} and #{end}
AND salesId=#{salesId}
GROUP BY day
ORDER BY day

SELECT sum(salesCommission) commission, MONTH(tradingTime) month
FROM auth_order
WHERE tradingTime between #{start} and #{end}
AND salesId=#{salesId}
GROUP BY month
ORDER BY month

SELECT category, count(category) AS count, DAYOFMONTH(tradingTime) day
FROM auth_order
WHERE tradingTime between #{start} and #{end}
GROUP BY category, day;

SELECT sum(price) price, sum(txFee) txFee, sum(deposit) deposit,
      sum(ldCommission) ldCommission, sum(dealerCommission) dealerCommission,
      sum(salesCommission) salesCommission, MONTH(tradingTime) date
FROM auth_order
WHERE tradingTime between #{start} and #{end}
GROUP BY date;

SELECT sum(price) price, sum(txFee) txFee, sum(deposit) deposit,
       sum(ldCommission) ldCommission, sum(dealerCommission) dealerCommission,
       sum(salesCommission) salesCommission, DAYOFMONTH(tradingTime) date
FROM auth_order
WHERE tradingTime between #{start} and #{end}
GROUP BY date;
