mapper.select:
SELECT COUNT(*) count
FROM auth_register_user
WHERE username=#{username}

SELECT COUNT(*) count
FROM auth_register_user
WHERE mac=#{mac}

SELECT COUNT(*) count
FROM auth_register_user
WHERE email=#{email}

mapper.update:
UPDATE IGNORE auth_register_user
SET email_status=1, token=#{token}, active_time=current_timestamp
WHERE username=#{username}

UPDATE IGNORE auth_register_user
SET email_status=1, token=#{token}, active_time=current_timestamp
WHERE id=#{id}

mapper.select:
SELECT COUNT(*) count
FROM auth_register_user
WHERE username=#{username} AND password=#{password}

mapper.update:
UPDATE IGNORE auth_register_user
SET token=#{token}
WHERE username=#{username}

UPDATE IGNORE auth_register_user
SET password=#{password}
WHERE username=#{username}

UPDATE IGNORE auth_register_user
SET token=#{token}, mac=#{mac}, btv=1
WHERE username=#{username}

UPDATE IGNORE auth_register_user
SET status=#{status}
WHERE id=#{id}

mapper.select:

WHERE id > #{start}
AND level > 1
AND expires_time  <  now();

WHERE id > 11000 AND mac in
#{mac}
ORDER BY id

mapper.update:
UPDATE IGNORE auth_register_user
SET level=#{level}, expires_time=#{expiresTime}
WHERE id=#{id}

mapper.select:
SELECT count(*) count, month(expires_time) month
FROM auth_register_user
WHERE level=#{salesId}
    AND id > 100
      AND expires_time BETWEEN #{start} AND #{end}
GROUP BY month;

SELECT count(*) volume, month(create_time) month
FROM auth_register_user
WHERE create_time BETWEEN #{start} AND #{end}
    AND id > 100
GROUP BY month;

SELECT count(*) volume, dayofmonth(create_time) day
FROM auth_register_user
WHERE create_time BETWEEN #{start} AND #{end}
    AND id > 100
GROUP BY day;
