mapper.update:
UPDATE auth_rent_user
SET  status='activate'
WHERE clientKey=#{_parameter}

UPDATE auth_rent_user
SET  status='deactivate'
WHERE clientKey=#{_parameter}

UPDATE auth_rent_user
SET  status='limited'
WHERE clientKey=#{_parameter}

UPDATE auth_rent_user
SET  status='canceled'
WHERE clientKey=#{_parameter}

mapper.select:
SELECT COUNT(*) FROM auth_rent_user
WHERE clientKey=#{clientKey}
AND mac=#{mac}

mapper.update:
UPDATE auth_rent_user
        SET auth_rent_user.status=#{status}
WHERE auth_rent_user.clientKey=#{clientKey}

UPDATE auth_rent_user
SET country=#{country}, region=#{region}, city=#{city}, timeZone=#{timeZone}, lastOnLineTime=now()
WHERE clientKey=#{clientKey}

mapper.select:
SELECT category, COUNT(category) AS count, DAYOFMONTH(activateTime) day
FROM auth_rent_user
WHERE activateTime BETWEEN #{start} AND #{end}
AND status!='canceled'
GROUP BY  category, day;

SELECT country AS name, COUNT(clientKey) AS value
FROM auth_rent_user
GROUP BY  name;
