mapper.update:
UPDATE auth_rent_user
SET  status='activate', activateTime=now()
WHERE clientKey=#{_parameter}

mapper.select:
SELECT COUNT(*) FROM auth_rent_user
WHERE clientKey=#{clientKey}
AND mac=#{mac}

mapper.update:
UPDATE auth_rent_user
SET country=#{country}, region=#{region}, city=#{city}, timeZone=#{timeZone}, lastOnLineTime=now()
WHERE clientKey=#{clientKey}
