mapper.select:
SELECT sum(amount) amount, sum(tx_fee) txFee, sum(deposit) deposit,
sum(ld_commission) ldCommission, sum(dealer_commission) dealerCommission,
sum(sales_commission) salesCommission, MONTH(create_time) date
FROM authorize_transaction
WHERE create_time BETWEEN #{start} AND #{end}
GROUP BY date;

SELECT sum(amount) amount, sum(tx_fee) txFee, sum(deposit) deposit,
sum(ld_commission) ldCommission, sum(dealer_commission) dealerCommission,
sum(sales_commission) salesCommission, DAYOFMONTH(create_time) date
FROM authorize_transaction
WHERE create_time BETWEEN #{start} AND #{end}
GROUP BY date;

SELECT sum(sales_commission) commission, DAYOFMONTH(create_time) day
FROM authorize_transaction
WHERE create_time between #{start} and #{end}
AND sales_id=#{salesId}
GROUP BY day
ORDER BY day

SELECT sum(sales_commission) commission, MONTH(create_time) month
FROM authorize_transaction
WHERE create_time between #{start} and #{end}
AND sales_id=#{salesId}
GROUP BY month
ORDER BY month
